SELECT NOMBREARTÍCULO, SECCIÓN, PRECIO, ROUND(PRECIO-(PRECIO*0.07),2) AS DESCUENTO_7 FROM PRODUCTOS;
SELECT NOMBREARTÍCULO, FECHA, SECCIÓN, PRECIO, ROUND(PRECIO-2,2) AS DT02_CERÁMICA FROM PRODUCTOS WHERE SECCIÓN="CERÁMICA" GROUP BY FECHA DESC;
SELECT NOMBREARTÍCULO,SECCIÓN,PRECIO, ROUND(PRECIO*1.02,2) AS PRECIO_AUMENTADO_2 FROM PRODUCTOS WHERE SECCIÓN="FERRETERÍA";